var link=document.querySelector(".application-title"),form=document.querySelector(".application"),arrival=form.querySelector("[name=arrival-date]"),departure=form.querySelector("[name=departure-date]");link.addEventListener("click",function(e){form.classList.contains("application-show")?(e.preventDefault(),form.classList.remove("application-show")):(e.preventDefault(),form.classList.add("application-show"),arrival.focus())}),form.addEventListener("submit",function(e){arrival.value&&departure.value||(e.preventDefault(),console.log("Нужно ввести дату заезда и выезда"))}),window.addEventListener("keydown",function(e){27===e.keyCode&&form.classList.contains("application-show")&&form.classList.remove("application-show")});